package qa1to10;

public class first {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  byte b = 100;
	        short s = 10000;
	        int i = 100000;
	        long l = 10000000000L;
	        float f = 3.14f;
	        double d = 3.14159265359;
	        char c = 'A';
	        System.out.println("the value of b "+b+" and the size is "+Byte.SIZE+" Bits");
	        System.out.println("the value of s "+s+" and the size is "+Short.SIZE+" Bits");
	        System.out.println("the value of i "+i+" and the size is "+Integer.SIZE+" Bits");
	        System.out.println("the value of l "+l+" and the size is "+Long.SIZE+" Bits");
	        System.out.println("the value of f "+f+" and the size is "+Float.SIZE+" Bits");
	        System.out.println("the value of d "+d+" and the size is "+Double.SIZE+" Bits");
	        System.out.println("the value of c "+c+" and the size is "+Character.SIZE+" Bytes");

	}

}
